var acctinfo = Class.create();
acctinfo.prototype = Object.extendsObject(AbstractAjaxProcessor, {

getDetails: function()
{
var arr = [];

var acct_number=this.getParameter('sysparm_visio_365_kfs_account_number'); //Value Fetched from client script

var gr=new GlideRecord('x_58872_needit_kfs_pinnacle');
   gr.addQuery('sys_id','visio_365_kfs_account_number');
gr.query();

while(gr.next())
{
arr.push(gr.account_name);
arr.push(gr.active_flag);
}


   //gs.addInfoMessage(arr);
return arr.toString();


},

});
