catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    // Call the GA function
    var ga = new GlideAjax('commonUtils');
    ga.addParam('sysparm_name', "getUserInfo");
    ga.addParam('sysparm_account_number', newValue); //Onchange field is reference field to sys_user table
    ga.getXMLAnswer(function(answer) {
        var response = JSON.parse(answer);

        g_form.setValue('visio_365_account_name', response.deptname); //used for string field
        g_form.setValue('visio_365_account_name', response.dept); //used for reference field to department table

        form.setValue('visio_365_kfs_account_org_code', response.managername); //used for string field
        g_form.setValue('visio_365_kfs_account_org_code', response.manager); //used for reference field to user table

        g_form.setValue('visio_365_rollup_org_code', response.locationname); //used for string field
        g_form.setValue('visio_365_rollup_org_code', response.location); //used for reference field to location table

    });
}
