catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below

    var accountNumber = g_form.getDisplayBox('visio_365_kfs_account_number').value;

    var ga = new GlideAjax('u_getAccountName'); //this is the script include
    ga.addParam('sysparm_name', 'getAccountInfo'); //this is the function within the script include
    ga.addParam('sysparm_accountNumber', accountNumber);
    ga.getXML(getResponse);


    function getResponse(response) {
        //set u_department_head sys_id to variable data
        var data = response.responseXML.documentElement.getAttribute('answer');

        g_form.setValue("visio_365_account_name", data);
    }

}
