sys_script_include.script:
var commonUtils = Class.create();
commonUtils.prototype = Object.extendsObject(AbstractAjaxProcessor, {

	getUserInfo: function(){
		var user = this.getParameter('sysparm_account_number');
		var gr = new GlideRecord("x_58872_needit_kfs_pinnacle");
		gr.get(user);

		var deptname = gr.account_name.getDisplayValue();
		var dept = gr.getValue('account_name');
		var manager = gr.getValue('org_cd');
		var managername = gr.org_cd.getDisplayValue();
		var location = gr.getValue('rpts_to_org_cd');
		var locationname = gr.rpts_to_org_cd.getDisplayValue();

		var response = {};
		response.deptname = deptname;
		response.dept = dept;
		response.manager = manager;
		response.managername = managername;
		response.location = location;
		response.locationname = locationname;

		return JSON.stringify(response);
	},

	type: 'commonUtils'
});
