sys_script_include.script:
var u_getAccountName = Class.create();
u_getAccountName.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    //function name
    getAccountInfo: function() {

        // search parameter for query - got this from client script
        var accountNumber = this.getParameter('sysparm_accountNumber');

        //query
        var gr = new GlideRecord('x_58872_needit_kfs_pinnacle');
        gr.addQuery('account_number','=', accountNumber); //look in the account_number column in the table and find the account number
        gr.query();
        if (gr.next()) {
            //return data
            return gr.account_name; //gimme the account name based on the row where the account number is located
        }
    }

});
