sys_script_include.script:
var si_getAllAccountInfo = Class.create();
si_getAllAccountInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    // parameter is used when the script is not called via GlideAjax
    getUserData: function() {
        var userSysId = this.getParameter('sys_parm_user_sys_id');
        var user = {}; // object that will be filled in
        var grUser = new GlideRecord('x_58872_needit_kfs_pinnacle');
        grUser.get(userSysId);

        // add multiple information to single object
        user.name = grUser.account_name.getDisplayValue().toString();
        user.orgCode = grUser.org_cd.getDisplayValue().toString();
        user.rollup = grUser.rpts_to_org_cd.getDisplayValue().toString();

        // return JSON string of the object
        return JSON.stringify(user);

    },

    type: 'si_getAllAccountInfo'
});
